import React, { Component } from 'react'
import CourseDataService from '../service/DataService';

const INSTRUCTOR = 'rewardTest'

class ListCustomerComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            customers: [],
            message: null
        }
        this.deleteClicked = this.deleteClicked.bind(this)
        this.updateClicked = this.updateClicked.bind(this)
        this.addClicked = this.addClicked.bind(this)
        this.refreshRecords = this.refreshRecords.bind(this)
    }

    componentDidMount() {
        this.refreshRecords();
    }

    refreshRecords() {
        DataService.retrieveAllCustomers(INSTRUCTOR)//HARDCODED
            .then(
                response => {
                    //console.log(response);
                    this.setState({ customer: response.data })
                }
            )
    }

    deleteClicked(id) {
        DataService.deleteCustomer(INSTRUCTOR, id)
            .then(
                response => {
                    this.setState({ message: `Delete of course ${id} Successful` })
                    this.refreshCustomer()
                }
            )

    }

    addClicked() {
        this.props.history.push(`/customers/-1`)
    }

    updateClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/customers/${id}`)
    }

    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>All Customers</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Product</th>
                                <th>Transaction Amount</th>
                                <th>Rewards</th>

                                <th>Update</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.customers.map(
                                    customer =>
                                        <tr key={customer.id}>
                                            <td>{customer.id}</td>
                                            <td>{customer.product}</td>
                                            <td>{customer.transAmount}</td>
                                              <td>{customer.rewards}</td>

                                            <td><button className="btn btn-success" onClick={() => this.updateClicked(customer.id)}>Update</button></td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteClicked(customer.id)}>Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ListCustomerComponent
